  const [pokemonSprite, setPokemonSprite] = useState(null);
  useEffect(() => {
    getPokemonSprite();
  }, []);

  const getPokemonSprite = async () => {
    const { data } = await axios.get(
      `http://localhost:8080/pokemonPic/${pokemon.pokemon.name}`
    );
    setPokemonSprite(data.sprites.front_default);
  };

  const walkPokemon = pokemon.filter((pokemon) => {
    return pokemon.version_details.some((detail) => {
      return detail.version.name === "pearl";
    });
  });
  const filterWalk = (pokemon) => {
    if (pokemon.version_details[0].version.name === "pearl") {
      pokemon.map((pokemon) => {
        return pokemon.version_details[0].encounter_details.filter((detail) => {
          return detail.method.name === "walk";
        });
      });
    } else {
      pokemon.map((pokemon) => {
        return pokemon.version_details[1].encounter_details.filter((detail) => {
          return detail.method.name === "walk";
        });
      });
    }
  };

