          return details.version.name === version;
        });
      })
      .map((pokemon) => {
        let pokemonDetails = {};
        pokemonDetails.name = pokemon.pokemon.name;
        pokemonDetails.versionDetails = pokemon.version_details.filter(
          (detail) => {
            return detail.version.name === version;
          }
        );
        return pokemonDetails;
      });
  };

