import StandardWalk from "../MorningWalk/MorningWalk";

function Walk({ pokemon }) {
  const filterCondition = (pokemon, condition1, condition2) => {
    return pokemon.filter((p) => {
      if (p.version_details[0].version.name === "pearl") {
        return p.version_details[0].encounter_details.some((detail) => {
          return (
            (detail.condition_values[0] &&
              detail.condition_values[0].name === condition1) ||
            detail.condition_values[0].name === condition2
          );
        });
      } else {
        return p.version_details[1].encounter_details.some((detail) => {
          return (
            (detail.condition_values[0] &&
              detail.condition_values[0].name === condition1) ||
            detail.condition_values[0].name === condition2
          );
        });
      }
    });
  };

  const standardWalkPokemon =
    pokemon && filterCondition(pokemon, "swarm-no", "radar-off");
  const dayWalkPokemon = pokemon && filterCondition(pokemon, "time-day");
  const nightWalkPokemon = pokemon && filterCondition(pokemon, "time-night");

  console.log(pokemon);
